import Cards from "@/components/Cards";
import FormCard from "@/components/FormCard";
export default {
  name: 'App',
  components: {
    Cards,
    FormCard
  },

  data() {
    return {
      name: '',
      description: '',
      image: '',
      price: '',
      cards: [],
      disabled: true
    };
  },

  methods: {
    add() {
      let newCard = {
        name: this.name,
        description: this.description,
        image: this.image,
        price: this.price
      };
      this.cards.push(newCard);
      this.name = '';
      this.description = '';
      this.image = '';
      this.price = '';
    },

    deleteCard(cardRemove) {
      this.cards = this.cards.filter(t => t !== cardRemove);
    },

    color() {
      if (this.name.length && this.description.length && this.image.length && this.price.length) {
        return 'colorGreen';
      }
    },

    disable() {
      if (this.name.length && this.description.length && this.image.length && this.price.length) {
        return false;
      } else return true;
    },

    edit(elem) {
      this.name = elem.name;
      this.description = elem.description;
      this.image = elem.image;
      this.price = elem.price;
    }

  }
};
