import Cards from "@/components/Cards";
import FormCards from "@/components/FormCards";
export default {
  components: {
    Cards,
    FormCards
  },

  data() {
    return {
      cards: [{
        id: 1,
        name: "rfckrk",
        description: "rcmkrm",
        image: "kokr",
        price: '12 000'
      }, {
        id: 2,
        name: "rfckrk",
        description: "rcmkrm",
        image: "kokr",
        price: '12 000'
      }, {
        id: 3,
        name: "rfckrk",
        description: "rcmkrm",
        image: "kokr",
        price: '12 000'
      }],
      disabled: true
    };
  },

  methods: {
    add(card) {
      this.cards.push(card);
    },

    edit(elem) {
      this.name = elem.name;
      this.description = elem.description;
      this.image = elem.image;
      this.price = elem.price;
    },

    removeCard(card) {
      this.cards = this.cards.filter(t => t.id !== card.id);
    }

  }
};
